import { HttpClient } from '@angular/common/http';
import { Component, EventEmitter, Inject, Input, OnInit, Output } from '@angular/core';
import { FormBuilder, FormControl, FormGroup, Validators } from '@angular/forms';
import { MAT_DIALOG_DATA } from '@angular/material/dialog';

interface Pessoa {
  nome: string;
  email: string;
  senha: string;
  cep: string;
  logradouro: string;
}

@Component({
  selector: 'app-formulario',
  templateUrl: './formulario.component.html',
  styleUrls: ['./formulario.component.scss'],
})
export class FormularioComponent implements OnInit {
  @Input() pessoaEditar!: Pessoa;
  @Output() dadosAtualizados = new EventEmitter<Pessoa>();
  pessoas: Pessoa[] = [];

  constructor(private http: HttpClient, @Inject(MAT_DIALOG_DATA) public pessoa: Pessoa, private formBuilder: FormBuilder) { }

  ngOnInit(): void {
  }

  buscarCep(cep: string) {
    const url = `https://viacep.com.br/ws/${cep}/json/`;
    this.http.get(url).subscribe((data: any) => {
      this.form.get('logradouro')?.setValue(data.logradouro);
    });
  }

  adicionarPessoa() {
    if (this.form.valid) {
      try {
        const pessoa: Pessoa = {
          nome: this.form.get('nome')!.value!,
          email: this.form.get('email')!.value!,
          senha: this.form.get('senha')!.value!,
          cep: this.form.get('cep')!.value!,
          logradouro: this.form.get('logradouro')!.value!
        };
        this.pessoas.push(pessoa);

        this.form.reset();
      }
      catch (err) {
        console.log('Erro ao enviar o formulário!', err);
      }
    } else {
      alert('Formulário inválido!');
    }
  }

  editarPessoa() {
    this.form = this.formBuilder.group({

    })
  }
}
<!DOCTYPE html>
<link
  href="https://fonts.googleapis.com/icon?family=Material+Icons"
  rel="stylesheet"
/>

<div>
  <mat-card>
    <mat-card-content>
      <form
        class="formulario"
        [formGroup]="form"
        (ngSubmit)="adicionarPessoa()"
      >
        <h2 class="nome">Nome</h2>
        <mat-form-field class="full-width-input">
          <input
            matInput
            placeholder="Digite o nome"
            formControlName="nome"
            required
            minlength="3"
            maxlength="30"
          />
          <mat-error> Nome é obrigatório </mat-error>
        </mat-form-field>
        <mat-form-field class="full-width-input">
          <input
            type="email"
            matInput
            placeholder="Digite o e-mail"
            formControlName="email"
            required
          />
          <mat-error> E-mail é obrigatório</mat-error>
        </mat-form-field>
        <mat-form-field class="full-width-input">
          <input
            matInput
            type="password"
            placeholder="Digite a senha"
            formControlName="senha"
            required
          />
          <mat-error> Senha é obrigatório </mat-error>
        </mat-form-field>
        <mat-form-field class="full-width-input">
          <input
            matInput
            placeholder="Digite o CEP"
            formControlName="cep"
            required
          />
          <mat-error> CEP é obrigatório </mat-error>
        </mat-form-field>
        <mat-form-field class="full-width-input">
          <input
            matInput
            placeholder="Logradouro"
            formControlName="logradouro"
            required
          />
          <mat-error> Logradouro é obrigatório </mat-error>
        </mat-form-field>
        <button mat-raised-button color="primary" (click)="adicionarPessoa()">
          {{ pessoa ? "Editar" : "Adicionar" }}
        </button>
      </form>
    </mat-card-content>
  </mat-card>
</div>
