import { HttpClient } from '@angular/common/http';
import { Component, OnInit } from '@angular/core';
import { FormControl, FormGroup, Validators } from '@angular/forms';

interface Pessoa {
  nome: string;
  email: string;
  senha: string;
  cep: string;
  logradouro: string;
}

@Component({
  selector: 'app-formulario',
  templateUrl: './formulario.component.html',
  styleUrls: ['./formulario.component.scss'],
})
export class FormularioComponent implements OnInit {
  pessoas: Pessoa[] = [];

  constructor(private http: HttpClient) { }

  form = new FormGroup({
    nome: new FormControl("", [Validators.required, Validators.minLength(3), Validators.maxLength(30)]),
    email: new FormControl("", [Validators.required, Validators.email]),
    senha: new FormControl("", [Validators.required]),
    cep: new FormControl("", [Validators.required, Validators.minLength(8), Validators.maxLength(8)]),
    logradouro: new FormControl({ value: "", disabled: true }, [Validators.required]),
  });

  ngOnInit(): void {
  }

  buscarCep(cep: string) {
    const url = `https://viacep.com.br/ws/${cep}/json/`;
    this.http.get(url).subscribe((data: any) => {
      this.form.get('logradouro')?.setValue(data.logradouro);
    });
  }

  adicionarPessoa() {
    if (this.form.valid) {
      const pessoa: Pessoa = {
        nome: this.form.get('nome')!.value!,
        email: this.form.get('email')!.value!,
        senha: this.form.get('senha')!.value!,
        cep: this.form.get('cep')!.value!,
        logradouro: this.form.get('logradouro')!.value!
      };
      this.pessoas.push(pessoa);

      this.form.reset();
    } else {
      alert('Formulário inválido');
    }
  }
}
<!DOCTYPE html>
<link
  href="https://fonts.googleapis.com/icon?family=Material+Icons"
  rel="stylesheet"
/>

<div>
  <div class="flip-card-inner">
    <div class="flip-card-front">
      <mat-card>
        <mat-card-content>
          <form
            class="formulario"
            [formGroup]="form"
            (ngSubmit)="adicionarPessoa()"
          >
            <h2 class="nome">Nome</h2>
            <mat-form-field class="full-width-input">
              <input
                matInput
                placeholder="Digite o nome"
                formControlName="name"
                required
              />
              <mat-error>
                obrigatório, tamanho mínimo de 3 carácteres, tamanho máx 30
              </mat-error>
            </mat-form-field>
            <mat-form-field class="full-width-input">
              <input
                matInput
                placeholder="Email"
                formControlName="email"
                required
              />
              <mat-error> Please provide a valid email address</mat-error>
            </mat-form-field>
            <mat-form-field class="full-width-input">
              <input
                matInput
                placeholder="Tarefa"
                formControlName="title"
                required
              />
              <mat-error> Please provide a task </mat-error>
            </mat-form-field>
            <button
              mat-raised-button
              color="primary"
              (click)="adicionarPessoa()"
            >
              Create Todo
            </button>
          </form>
        </mat-card-content>
      </mat-card>
    </div>
  </div>
</div>
