import { HttpClient } from '@angular/common/http';
import { Component, OnInit } from '@angular/core';
import { FormControl, FormGroup } from '@angular/forms';

@Component({
  selector: 'app-formulario',
  templateUrl: './formulario.component.html',
  styleUrls: ['./formulario.component.scss']
})
export class FormularioComponent implements OnInit {  
  constructor(private http: HttpClient) { }

  form = new FormGroup({
    nome: new FormControl("Nome", []),
    email: new FormControl("meuemail", []),
    senha: new FormControl("password", []),
    cep: new FormControl("", []),
    logradouro: new FormControl("", []),
  })

  ngOnInit(): void {
  }

  buscarCep(cep: string) {
    const url = `https://viacep.com.br/ws/${cep}/json/`;
    return this.http.get(url);
  }
}
<form style="display: flex;flex-direction: column;gap: 20px;">
    <mat-form-field appearance="fill">
        <mat-label>Nome</mat-label>
        <input type="text" matInput placeholder="Digite o nome">
    </mat-form-field>
    <mat-form-field appearance="fill">
        <mat-label>Email</mat-label>
        <input type="text" matInput placeholder="Digite o e-mail">
    </mat-form-field>
    <mat-form-field appearance="fill">
        <mat-label>Senha</mat-label>
        <input type="password" matInput placeholder="Digite a senha">
    </mat-form-field>
    <mat-form-field appearance="fill">
        <mat-label>CEP</mat-label>
        <input type="text" matInput placeholder="Digite ">
    </mat-form-field>
    <mat-form-field appearance="fill">
        <mat-label>Logradouro</mat-label>
        <input type="text" matInput placeholder="Digite algo">
    </mat-form-field>
    <button mat-raised-button>Salvar</button>
    <button mat-raised-button matDialogClose>Cancelar</button>
</form>
