import { HttpClient } from '@angular/common/http';
import { Component, OnInit } from '@angular/core';
import { FormControl, FormGroup, Validators } from '@angular/forms';

interface Pessoa {
  nome: string;
  email: string;
  senha: string;
  cep: string;
  logradouro: string;
}

@Component({
  selector: 'app-formulario',
  templateUrl: './formulario.component.html',
  styleUrls: ['./formulario.component.scss']
})
export class FormularioComponent implements OnInit {
  pessoas: Pessoa[] = [];

  constructor(private http: HttpClient) { }

  form = new FormGroup({
    nome: new FormControl("", [Validators.required, Validators.minLength(3), Validators.maxLength(30)]),
    email: new FormControl("", [Validators.required, Validators.email]),
    senha: new FormControl("", [Validators.required]),
    cep: new FormControl("", [Validators.required, Validators.minLength(8), Validators.maxLength(8)]),
    logradouro: new FormControl({ value: "", disabled: true }, [Validators.required]),
  });

  ngOnInit(): void {
  }

  buscarCep(cep: string) {
    const url = `https://viacep.com.br/ws/${cep}/json/`;
    this.http.get(url).subscribe((data: any) => {
      this.form.get('logradouro')?.setValue(data.logradouro);
    });
  }

  adicionarPessoa() {
    if (this.form.valid) {
      const pessoa: Pessoa = {
        nome: this.form.get('nome')!.value!,
        email: this.form.get('email')!.value!,
        senha: this.form.get('senha')!.value!,
        cep: this.form.get('cep')!.value!,
        logradouro: this.form.get('logradouro')!.value!
      };
      this.pessoas.push(pessoa);

      this.form.reset();
    } else {
      alert('Formulário inválido');
    }
  }
}
<form
  style="display: flex; flex-direction: column; gap: 20px"
  (ngSubmit)="adicionarPessoa()"
>
  <mat-form-field appearance="fill">
    <mat-label>Nome</mat-label>
    <input
      type="text"
      matInput
      placeholder="Digite o nome"
      formControlName="nome"
    />
    <mat-error *ngIf="form.get('nome').hasError('required')"
      >Nome é obrigatório</mat-error
    >
    <mat-error *ngIf="form.get('nome').hasError('minlength')"
      >Nome deve ter no mínimo 3 caracteres</mat-error
    >
    <mat-error *ngIf="form.get('nome').hasError('maxlength')"
      >Nome não pode ter mais de 30 caracteres</mat-error
    >
  </mat-form-field>
  <mat-form-field appearance="fill">
    <mat-label>Email</mat-label>
    <input
      type="email"
      matInput
      placeholder="Digite o e-mail"
      formControlName="form.get('email')"
    />
    <mat-error>Email é obrigatório</mat-error
    >
    <mat-error>Email inválido</mat-error
    >
  </mat-form-field>
  <mat-form-field appearance="fill">
    <mat-label>Senha</mat-label>
    <input
      type="password"
      matInput
      placeholder="Digite a senha"
      formControlName="senha"
    />
    <mat-error>Senha é obrigatória</mat-error
    >
  </mat-form-field>
  <mat-form-field appearance="fill">
    <mat-label>CEP</mat-label>
    <input
      type="text"
      matInput
      placeholder="Digite o CEP"
      formControlName="cep"
      (input)="buscarCep($event.target.value)"
    />
    <mat-error>CEP é obrigatório</mat-error
    >
  </mat-form-field>
  <mat-form-field appearance="fill">
    <mat-label>Logradouro</mat-label>
    <input
      type="text"
      matInput
      placeholder="Digite o logradouro"
      formControlName="logradouro"
    />
    <mat-error>Logradouro é obrigatório</mat-error
    >
  </mat-form-field>
  <button type="submit" mat-raised-button [disabled]="form.invalid">
    Salvar
  </button>
  <button type="button" mat-raised-button matDialogClose>Cancelar</button>
</form>
